import React, { useEffect, useState } from "react";
import '../css/ListBands.css'
import "bootstrap/dist/css/bootstrap.min.css";
import { Button } from "react-bootstrap";
import Loading from '../components/Loading'


const ListBands = () => {    
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState([]);
  const baseUrl = "https://my-json-server.typicode.com/improvein/dev-challenge";

  const changeState = () => {
    setLoading(false);
    setTimeout(()=>{
        setLoading(false)
    }, 5000);
}

  useEffect(() => {
    const fetchData = async () => {
      await fetch(`${baseUrl}/bands`)
        .then((response) => response.json())
        .then((json) => setData(json))
        .catch((error) => console.log(error));
    };
    fetchData();
  });

  const ShowInfo = e => {
    document.getElementById('show').style.display = 'inline-block'
  }

  if(loading){
    return(
      <Loading/>
    )
  }

  return (
      <div>
      <h2 id="bands-title">Bands: {data.length}</h2>
      <br/>
      {data.map((band, idx) => (
          <div className="list-bands">
        <h3 key={`key_${band.id}`}>{idx+1} - {band.name}
        <Button onClick={() => ShowInfo()} className="btn btn-danger">▼</Button></h3>
        <p id="show" style={{display:'none'}} key={`key_${band.id}`}>{idx+1} - {band.name}</p>
        <button onClick={()=>changeState()}></button>
        </div>
      ))}
      <br/>
      </div>
  )
};

export default ListBands;
