import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const Bands = () => {
  const {id} = useParams()
  console.log(useParams())
  const [infoBands, setInfoBands] = useState([]);
  const [membersBand, setMembers] = useState([]);

  useEffect(() => {
    obtainDatas()
    obtainMembers()
  }, [])

  const obtainDatas = async () => {
    const data = await fetch( `https://my-json-server.typicode.com/improvein/dev-challenge/bands/${id}`)
    const users = await data.json();
    setInfoBands(users);
  };

  const obtainMembers = async () => {
    const data = await fetch( `https://my-json-server.typicode.com/improvein/dev-challenge/bands/${id}/members`)
    const memb = await data.json();
    setMembers(memb.name);
  };


  return (
  <div className="container-bands">
      <h1>Band name: {id}</h1>
      <h1>{infoBands.name}</h1>
      <h1>{infoBands.year}</h1>
      <h1>{infoBands.country}</h1>
      <h1>{infoBands.genreCode}</h1>
      <h1>{membersBand}</h1>
  </div>
    )
};

export default Bands;
