/* eslint-disable jsx-a11y/alt-text */
import React from "react";
import "../css/Login.css";
import Image from "../images/music-logo.png";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import md5 from "md5";
import Cookies from "universal-cookie";
import { Button,Modal } from 'react-bootstrap'

const baseUrl = "http://localhost:3001/user"; //Momentáneo, luego subir a un host el JSON


class Login extends React.Component {
  state = {
    form: {
      username: "",
      password: "",
    },
  };

  constructor(){
    super()
    this.state={
      show:false,
    }
  }

  handleModalSuccess(){
    this.setState({show:!this.state.show})
  }

  handleModalFailed(){
    this.setState({show:!this.state.show})
  }

  handleChange = async (e) => {
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value,
      },
    });
  };

  logIn = async () => {
    await axios
      .get(baseUrl, {
        params: {
          username: this.state.form.username,
          password: md5(this.state.form.password),
        },
      })

      .then((response) => {
        return response.data;
      })

      .then((response) => {
        if (response.length > 0) {
            this.handleModalSuccess();
        } else {
            this.handleModalFailed();
        }
      })

      .catch((error) => {
        return error;
      });
  };

  render() {
    return (
      <div className="container">

             <Modal show={this.state.show}>
            <Modal.Header>Welcome!</Modal.Header>
          <Modal.Body>You will be redirected to the home page...</Modal.Body>
          <Modal.Footer/>
          <Button onClick={()=>{this.handleModalSuccess()}}>Continue</Button>
        </Modal>

        <Modal show={this.state.show}>
            <Modal.Header>Login failed</Modal.Header>
          <Modal.Body>Username or password are invalids</Modal.Body>
          <Modal.Footer/>
          <Button onClick={()=>{this.handleModalFailed()}}>Continue</Button>
        </Modal>

        <div className="image-logo">
          <img id="music-logo" src={Image}></img>
        </div>
        <div className="principal">
          <div className="secondary">
            <div className="form-group">
              <label>Username: </label>
              <input
                name="username"
                onChange={this.handleChange}
                type="text"
                className="form-control"
              />
              <br />

              <label>Password: </label>
              <input
                name="password"
                onChange={this.handleChange}
                type="password"
                className="form-control"
              />
              <br />

              <button onClick={() => this.logIn()} className="btn btn-light">
                Login
              </button>
              <button className="btn btn-danger">Register</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
