import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const Bands = () => {
  const {name} = useParams()
  const [albums, setAlbums] = useState([])

  useEffect(() => {
    obtainData()
  },[])

  const obtainData = async () => {
    const data = await fetch(`https://my-json-server.typicode.com/improvein/dev-challenge/db/${name}`);
    const albumsName = await data.json()
    setAlbums(albumsName)
  };

  return (
  <div className="container-bands">
      Band name: {name}
      <h3>{albums.name}</h3>
      <h3>{albums.year}</h3>

  </div>
    )
};

export default Bands;
