import React from 'react'
import { FaArrowCircleRight, FaArrowCircleDown} from "react-icons/fa";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";

export default function TableBands(props){
    return(
        <Table id="table-data" striped bordered hover variant="dark">
            <thead>
                <tr id="first-tr">
                    <th>#</th>
                    <th id="th-second">Name <FaArrowCircleDown/></th>
                    <th>Year</th>
                </tr>
            </thead>
            <tbody>
            {
            props.data.map((item, idx) => (
                <tr>
                  <td>{idx + 1}</td>
                  <td>{item.name}
                  <Link to={`/menu/${item.id}`}>
                <button className="btn-show">
                  <FaArrowCircleRight size="1em" />
                </button>
              </Link></td>
                  <td>{item.year}</td>
                </tr>
            ))
            }
            </tbody>
        </Table>
    )
};
