import React, { useState, useEffect } from 'react'
import { useParams } from "react-router-dom";

const Table = (props) =>  {
    const { bands } = props;
    const { id } = useParams();
    const [infoBands, setInfoBands] = useState([]);
        
    let sortedProducts = [...bands];
    sortedProducts.sort((a, b) => {
      if (a.name < b.name) {
        return -1;
      }
      if (a.name > b.name) {
        return 1;
      }
      return 0;
    });

    useEffect(() => {
        const obtainDatas = async () => {
          const data = await fetch(
            `https://my-json-server.typicode.com/improvein/dev-challenge/bands/${id}`
          );
          const users = await data.json();
          setInfoBands(users);    
          
        };
    
        obtainDatas();
      }, [id]);
    

    return (
      <table>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Year</th>
          </tr>
        </thead>
        <tbody>
          {bands.map(band => (
            <tr key={band.id}>
              <td>{band.name}</td>
              <td>{band.price}</td>
              <td>{band.stock}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }
  

  export default Table
