import React, { useState, useEffect } from "react";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Loading from "./Loading";
import Form from "react-bootstrap/Form";
import TableComponent from './TableBands'

const ListBands = () => {
  const [bands, setBands] = useState([]);
  const [allData, setAllData] = useState(false);  

  useEffect(() => {
    obtainData();
  }, []);

  const obtainData = async () => {
    const data = await fetch(
      "https://my-json-server.typicode.com/improvein/dev-challenge/db"
    );
    const bandsName = await data.json();
    setBands(bandsName.bands);
    setAllData(true);
  };

  return (
    <div className="container-list">
      <div id="bands-title">
        <p>´Total Bands: {bands.length}</p>
      </div>
      <fieldset className="check-container">
        <Form.Group>
          <Form.Check className="check" type="checkbox" label="Rock" />
          <Form.Check className="check" type="checkbox" label="Rock and Roll" />
          <Form.Check className="check" type="checkbox" label="Hard Rock" />
          <Form.Check className="check" type="checkbox" label="Grunge" />
          <Form.Check className="check" type="checkbox" label="Heavy Metal" />
          <Form.Check className="check" type="checkbox" label="Power Metal" />
          <Form.Check className="check" type="checkbox" label="Black Metal" />
          <Form.Check className="check" type="checkbox" label="ProgressMetal" />
        </Form.Group>
      </fieldset>
      {!allData ? (
        <Loading />
      ) : (
        <div className="list-bands">
          <ul>
          <TableComponent data={bands}/>
          </ul> 
        </div>
      )}
    </div>
  );
};

export default ListBands;
