import React, { useEffect, useState } from "react";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Loading from "../components/Loading";
import { FaArrowCircleDown } from "react-icons/fa";
import Form from "react-bootstrap/Form";
import axios from 'axios'

  const ListBands = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [data, setData] = useState([]);
  const [info, setInfo] = useState(false);
  const baseUrl = "https://my-json-server.typicode.com/improvein/dev-challenge";

  const showInfo = () => {
    setInfo(!info);
  };

  // useEffect(() => {
  //   const fetchData = async () => {
  //     const bands = `${baseUrl}/bands`;
  //     await fetch(bands)
  //       .then((response) => response.json())
  //       .then((json) => setData(json))
  //       .catch((error) => console.log(error));
  //   };
  //   fetchData();
  // });


  useEffect(() => {
    axios.get(`${baseUrl}/bands`)
    .then(res=> {
      const data = res.data
      setData(data.name)
    })
  })

  try {
    if(info===true){
      document.getElementById('show').style.display = 'block'
    }else if(info===false){
      document.getElementById('show').style.display = 'none'
    }
  } catch (error) {
}

    return (
         <div className="container-list">
            <fieldset className="check-container">
            <Form.Group>
              <Form.Label as="legend">
              </Form.Label>
              <Form.Check
                type="radio"
                label="Bands"
                className="check"
                name="formHorizontalRadios"
                id="formHorizontalRadios1"
              />
              <Form.Check
                type="radio"
                label="Genres"
                className="check"
                name="formHorizontalRadios"
                id="formHorizontalRadios2"
              />
              <Form.Check
                type="radio"
                label="Years"
                className="check"
                name="formHorizontalRadios"
              />
            </Form.Group>
          </fieldset>

           <div>
          <Form id="search">
          <Form.Group>
            <Form.Control
            type="search" 
            placeholder="Search by band..üîç"  
            onChange={(event) => { setSearchTerm(event.target.value); }} />
            <br/>
          </Form.Group>
        </Form>
        </div>
        {/* {data.true}
        { !data ? <Loading/> :  */}
        {data.filter(band => {
          if(searchTerm === ""){
            return band
          }else if (band.name.toLowerCase().includes(searchTerm.toLowerCase())){
            return band
          }else {
            return(console.log("Error"))
          }
        }).map( (band,idx) =>{
          return(
          <div className="list-bands">
          <h3 key={`key_${band.id}`} code={`code_${band.genreCode}`} >
            {idx + 1} - {band.name} 
            <button className="btn-show" onClick={showInfo}><FaArrowCircleDown size="1em"/></button>
            <h4 id="show" style={{display:'none'}}> Genre:  <br/>  Year: {band.year} <br/>  Country: {band.country} <br/> {} </h4>
          </h3>
          <br/>
        </div>
        )})}
        <br />
      </div>
  );
}

export default ListBands;
