import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const Bands = () => {
  const {id} = useParams()
  const [bandYear, setBandYear] = useState([])

  useEffect(() => {
    obtainData()
  },[])

  const obtainData = async () => {
    const data = await fetch(`https://my-json-server.typicode.com/improvein/dev-challenge/db/bands/${id}`);
    const bandyear = await data.json()
    setBandYear(bandyear.year)
  };

  return (
  <div className="container-bands">
      <h1>Band name: {id}</h1>
    <h1>{bandYear.name}</h1>
  </div>
    )
};

export default Bands;
