/* eslint-disable jsx-a11y/alt-text */
import React from "react";
import "../css/Login.css";
import Image from "../images/music-logo.png";
import "bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
import md5 from "md5";
import Cookies from "universal-cookie";
import { Button,Modal } from 'react-bootstrap'

const baseUrl = "http://localhost:3001/user"; //Momentáneo, luego subir a un host el JSON
const cookies = new Cookies(); 
var nameLogin = '';

class Login extends React.Component {
  state = {
    form: {
      username: "",
      password: "",
    },
  };

  constructor(){
    super()
    this.state={
      show:false,
    }
  }

  handleModalSuccess(){
    this.setState({show:!this.state.show})
  }

  handleChange = async (e) => {
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value,
      },
    });
  };

  logIn = async () => {
    await axios
      .get(baseUrl, {
        params: {
          username: this.state.form.username,
          password: md5(this.state.form.password),
        },
      })

      .then((response) => {
        return response.data;
      })

      .then((response) => {
        if (response.length > 0) {
            var answer = response[0];
            cookies.set('id', answer.id, {path:"/"})
            cookies.set('last_name', answer.last_name, {path:"/"})
            cookies.set('name', answer.name, {path:"/"})
            cookies.set('username', answer.username, {path:"/"})
            nameLogin = answer.name;
            this.handleModalSuccess();
            document.getElementById('username-input').style.backgroundColor = "#66FF66";
            document.getElementById('username-input').style.color = "#000";
            document.getElementById('password-input').style.backgroundColor = "#66FF66";
            document.getElementById('password-input').style.color = "#000";
            document.getElementById('failed').style.display = 'none'

        } else{
            document.getElementById('username-input').style.backgroundColor = "#FF3333";
            document.getElementById('username-input').style.color = "#FFF";
            document.getElementById('password-input').style.backgroundColor = "#FF3333";
            document.getElementById('password-input').style.color = "#FFF";
            document.getElementById('failed').style.display = 'block'
       }
    })

      .catch((error) => {
        return error;
      });
  };

  render() {
    return (
      <div className="container">

             <Modal show={this.state.show}>
            <Modal.Header>Welcome! {nameLogin}</Modal.Header>
          <Modal.Body>You will be redirected to the home page...</Modal.Body>
          <Modal.Footer/>
          <Button onClick={()=>{this.handleModalSuccess()}}>Continue</Button>
        </Modal>

        <div className="image-logo">
          <img id="music-logo" src={Image}></img>
        </div>
        <div className="principal">
          <div className="secondary">
            <div className="form-group">
              <label>Username: </label>
              <input
                name="username"
                id="username-input"
                onChange={this.handleChange}
                type="text"
                className="form-control"
              />
              <br />

              <label>Password: </label>
              <input
                name="password"
                id="password-input"
                onChange={this.handleChange}
                type="password"
                className="form-control"
              />
              <br />

              <p id="failed" style={{display:'none'}}>Username or password are invalids.</p>
              <button onClick={() => this.logIn()} className="btn btn-light">
                Login
              </button>
              <button className="btn btn-danger">Register</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
