import React, { useEffect, useState } from "react";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Loading from "../components/Loading";
import { FaArrowCircleDown, FaArrowCircleLeft } from "react-icons/fa";
import Form from "react-bootstrap/Form";
import { Button } from "react-bootstrap";


  const ListBands = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [data, setData] = useState(false);
  const [info, setInfo] = useState(false);
  const baseUrl = "https://my-json-server.typicode.com/improvein/dev-challenge";

  const showInfo = () => {
    setInfo(!info);
  };

  useEffect(() => {
    const fetchData = async () => {
      const bands = `${baseUrl}/bands`;
      await fetch(bands)
        .then((response) => response.json())
        .then((json) => setData(json))
        .catch((error) => console.log(error));
    };
    fetchData();
  });

  try {
    if(info===true){
      document.getElementById('show').style.display = 'block'
    }else if(info===false){
      document.getElementById('show').style.display = 'none'
    }
  } catch (error) {
}

    return (
         <div className="container-list">
           <div>
          <Form id="search">
          <Form.Group>
          <Button className="btn btn-dark" method="get" onClick={""}> Search ğŸ”</Button>

            <Form.Control
            type="search" 
            placeholder="Search by band.." 
            onChange={(event) => { setSearchTerm(event.target.value); }} />
            <br/>
            <div className="search-container">
            <Button className="btn btn-dark" method="get" onClick={""}> Search ğŸ”</Button>
            </div>
          </Form.Group>
        </Form>
        </div>
        

        {data.true}
        { !data ? <Loading/> : 
        data.map( (band,idx) =>{
          return(
          <div className="list-bands">
          <h3 key={`key_${band.id}`} code={`code_${band.genreCode}`} >
            {idx + 1} - {band.name} 
            <button className="btn-show" onClick={showInfo}><FaArrowCircleDown size="1em"/></button>
            <h4 id="show" style={{display:'none'}}> Genre:  <br/>  Year: {band.year} <br/>  Country: {band.country} <br/> {} </h4>
          </h3>
          <br/>
        </div>
        )})}
        <br />
      </div>
  );
}
