import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "../css/ListBands.css";

const Bands = () => {
  const { id } = useParams();
  console.log(useParams());
  const [infoBands, setInfoBands] = useState([]);
  const [membersBand, setMembers] = useState([]);

  useEffect(() => {
    const obtainDatas = async () => {
      const data = await fetch(
        `https://my-json-server.typicode.com/improvein/dev-challenge/bands/${id}`
      );
      const users = await data.json();
      setInfoBands(users);
    };

    const obtainMembers = async () => {
      const data = await fetch(
        `https://my-json-server.typicode.com/improvein/dev-challenge/bands/${id}`
      );

      const memb = await data.json();
      setMembers(memb.members);
    };

    obtainDatas();
    obtainMembers();
  }, [id]);

  return (
    <div className="container-bands">
      <h1>Band name: {id}</h1>
      <h1>{infoBands.name}</h1>
      <h1>{infoBands.year}</h1>
      <h1>{infoBands.country}</h1>
      <h1>{infoBands.genreCode}</h1>
      <div className="members">
        <h2>{membersBand.map((member) => member.name )}</h2>
      </div>
    </div>
  );
};

export default Bands;
