import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const Bands = () => {
  const {id} = useParams()
  const [membersBand, setMembers] = useState([]);

  useEffect(() => {
    obtainData();
  }, []);

  const obtainData = async () => {
    const data = await fetch( `https://my-json-server.typicode.com/improvein/dev-challenge/${id}`);
    const users = await data.json();
    setMembers(users);
  };


  return (
  <div className="container-bands">
      <h1>Band name: {id}</h1>
      <h1>{membersBand.name}</h1>
  </div>
    )
};

export default Bands;
