import React from "react";
import { Button } from "react-bootstrap";
import Cookies from "universal-cookie";
import "../css/Menu.css";
import { Link } from "react-router-dom";
import { Modal } from "react-bootstrap";
import Form from 'react-bootstrap/Form';


const cookies = new Cookies();

class Menu extends React.Component {

  constructor() {
    super();
    this.state = {
      showmodalexit: false,
    };
  }

  handleModalLogOut() {
    this.setState({ showmodalexit: !this.state.showmodalexit });
  }

  logOutSession = () => {
    cookies.remove("id", { path: "/" });
    cookies.remove("last_name", { path: "/" });
    cookies.remove("name", { path: "/" });
    cookies.remove("username", { path: "/" });
    this.handleModalLogOut();
  };

  componentDidMount() {
    if(!cookies.get('username')){
        window.location.href="./"
    }
    
    fetch('https://my-json-server.typicode.com/improvein/dev-challenge/bands')
    .then(response => response.json())
    .then(namesJson => this.setState({names: namesJson.name}))
}

componentDidUpdate() {
}

  render() {
      console.log('id'+ cookies.get('id'));
      console.log('last_name'+ cookies.get('last_name'));
      console.log('name'+ cookies.get('id'));
      console.log('username'+ cookies.get('id'));

    return (
      <div className="container">
        <Modal show={this.state.showmodalexit}>
          <Modal.Header>Thanks!, See you soon</Modal.Header>
          <Modal.Body>Please click on the button to continue.</Modal.Body>
          <Modal.Footer />
          <Button
            className="btn btn-success"
            style={{ borderRadius: "0%" }}
            onClick={() => {
              this.handleModalLogOut();
            }}>
            <Link className="link" to="/">Continue</Link>
          </Button>
        </Modal>

        <div>
          <h1>Rock Bands !</h1>
        </div>

        <Form id="search">
          <Form.Group>
          <Form.Control type="search" placeholder="Search by band.." />
        </Form.Group>
      </Form>

        <div>
          <Button
            onClick={() => this.logOutSession()}
            className="btn btn-danger logout-btn">
            Logout
          </Button>
        </div>
      </div>
    );
  }
}

export default Menu;
