import React from 'react';
import { Button,  } from "react-bootstrap";
import Cookies from 'universal-cookie';
import '../css/Menu.css'
import { Redirect } from 'react-router-dom'
import { Modal } from "react-bootstrap";

const cookies = new Cookies();


class Menu extends React.Component {

    constructor() {
        super();
        this.state = {
          showmodalexit: false,
        };
      }
    
      handleModalLogOut() {
        this.setState({ showmodalexit: !this.state.showmodalexit });
      }

    logOutSession=()=>{
    cookies.remove('id', {path:'/'});
    cookies.remove('last_name', {path:'/'});
    cookies.remove('name', {path:'/'});
    cookies.remove('username', {path:'/'});
    this.handleModalLogOut();
    }

    render(){
        return(
            <div className="container">
                <Modal show={this.state.showmodalexit}>
          <Modal.Header>
            Thanks!, See you soon 
          </Modal.Header>
          <Modal.Body>Please click on the button to continue.</Modal.Body>
          <Modal.Footer />
          <Button className="btn btn-success" style={{borderRadius:'0%'}} onClick={() => { this.handleModalLogOut();}}>
          <Redirect to="/menu">Continue</Redirect>
          </Button>
        </Modal>
                <div>
                <h1>Rock Bands !</h1>
                </div>
                <div>
             <Button onClick={this.logOutSession()} className="btn btn-danger logout-btn">
                 Logout
             </Button></div>
            </div>
        )
    }
}

export default Menu;
