import React, {useState} from 'react'
import { FaArrowCircleRight, FaArrowCircleDown} from "react-icons/fa";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import Form from 'react-bootstrap/Form'

export default function TableBands(props){

  const [searchTerm, setSearchTerm] = useState("");
  const [sorting, setSorting] = useState({field: "", order:""})

    return(
      <div>
    <div className="search-container">
      <Form id="search">
      <Form.Group>
        <Form.Control
          type="search"
          placeholder="Search by band..üîç"
          onChange={(event) => {
            setSearchTerm(event.target.value);
          }}
        />
        <br />
      </Form.Group>
    </Form>
  </div>
        <Table id="table-data" striped bordered hover variant="dark">
            <thead>
                <tr id="first-tr">
                    <th>#</th>
                    <th id="th-second">Name 
                    <button id="btn-circle" onClick={() => props.sortBy('name')}>
                      <FaArrowCircleDown id="icon-circle" color="#fff"/>
                      </button></th>
                    <th>Year</th>
                </tr>
            </thead>
            <tbody>
            {props.data
              .filter((item) => {
                if (searchTerm === "") {
                  return item;
                } else if ( item.name.toLowerCase().includes(searchTerm.toLowerCase()) ) {
                  return item;
                } else {
                  return null;
                }
              })
              .map((item, idx) => (
                <tr>
                  <td>{idx + 1}</td>
                  <td>{item.name}
                  <Link to={`/menu/${item.id}`}>
                <button className="btn-show">
                  <FaArrowCircleRight size="1em" />
                </button>
              </Link></td>
                  <td>{item.year}</td>
                </tr>
            ))
            }
            </tbody>
        </Table>
        </div>
    )
};
