import React from 'react'
import TableBands from './TableBands'
import Loading from "./Loading";
import Form from "react-bootstrap/Form";
import "bootstrap/dist/css/bootstrap.min.css";

class ListBands extends React.Component{

  constructor(props){
    super(props)
    this.state={
      data:[],
      allData: false,
      direction: {
        id:'asc',
        year:'asc',
        name :'asc',
      },
    }

    this.sortBy = this.sortBy.bind(this)
  }

  sortBy(key){
    this.setState({
      data: this.state.data.sort( (a, b) => (
        this.state.direction[key] === 'asc'
        ? parseInt(a[key]) - parseInt(b[key])
        : parseInt(b[key]) - parseInt(a[key])
      )),
        direction: {
          [key]: this.state.direction[key] === 'asc' ? 'desc' : 'asc'
      }})
    }

  async componentDidMount() {
    await this.fetchData()
  }


  fetchData = async () => {
    let res = await fetch('https://my-json-server.typicode.com/improvein/dev-challenge/bands')
    let data = await res.json()
    this.setState({
      data
    })
  }

  
  render(){
    return(
      <div className="container-list">
            <div id="bands-title">
               <p>´Total Bands: {this.state.data.length}</p>
             </div>
             <fieldset className="check-container">
         <Form.Group>
           <Form.Check className="check" type="checkbox" label="Rock" />
           <Form.Check className="check" type="checkbox" label="Rock and Roll" />
           <Form.Check className="check" type="checkbox" label="Hard Rock" />
           <Form.Check className="check" type="checkbox" label="Grunge" />
           <Form.Check className="check" type="checkbox" label="Heavy Metal" />
           <Form.Check className="check" type="checkbox" label="Power Metal" />
           <Form.Check className="check" type="checkbox" label="Black Metal" />
           <Form.Check className="check" type="checkbox" label="ProgressMetal" />
         </Form.Group>
       </fieldset>
      
      <div className="page-container">
        {this.allData ? ( <Loading/> ) : (
        <TableBands
        data={this.state.data} sortBy={this.sortBy} />
      )}</div>
      </div>
    )
  }
}

export default ListBands;
