import React, { useState, useEffect } from 'react'

const Table = (props) =>  {
    const { bands } = props;
    const [bandsNames, setBandsNames] = useState([]);
  
    useEffect(() => {
      obtainData();
    }, []);
  
    const obtainData = async () => {
      const data = await fetch(
        "https://my-json-server.typicode.com/improvein/dev-challenge/db"
      );
      const bandsName = await data.json();
      setBandsNames(bandsName.bands);
    };
    
    
    let sortedProducts = [...bands];
    sortedProducts.sort((a, b) => {
      if (a.name < b.name) {
        return -1;
      }
      if (a.name > b.name) {
        return 1;
      }
      return 0;
    });


    return (
      <table>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Year</th>
          </tr>
        </thead>
        <tbody>
          {bandsNames.map(bandsNames => (
            <tr key={bandsNames.id}>
              <td>{bandsNames.name}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }
  

  export default Table
