import React, { useEffect, useState } from "react";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Button } from "react-bootstrap";
import Loading from "../components/Loading";
import { FaArrowCircleDown } from "react-icons/fa";


const ListBands = () => {
  const [loading, setLoading] = useState(false);
  const [data, setData] = useState([]);
  const [info, setInfo] = useState(false);
  const baseUrl = "https://my-json-server.typicode.com/improvein/dev-challenge";

  const changeState = () => {
    setLoading(true);
    setTimeout(()=>{
      setLoading(false)
    },1000)
  }

  const showInfo = () => {
    setInfo(!info);
  };

  useEffect(() => {
    const fetchData = async () => {
      await fetch(`${baseUrl}/bands`)
        .then((response) => response.json())
        .then((json) => setData(json))
        .catch((error) => console.log(error));
    };
    fetchData();
  });


  try {
    if(info===true){
      document.getElementById('show').style.display = 'block'
    }else if(info===false){
      document.getElementById('show').style.display = 'none'
    }
  } catch (error) {
}

 
  if (loading) {
    return (
    <Loading />
    )

  } else {
    return (
      <div>
        <div><Button className="btn btn-light" onClick={()=>changeState()}>Refresh</Button></div>
        <h2 id="bands-title">Bands: {data.length}</h2>
        <br />
        {data.map((band, idx) => (
          <div className="list-bands">
            <h3 key={`key_${band.id}`}>
              {idx + 1} - {band.name}
              <Button onClick={showInfo}><FaArrowCircleDown/></Button>
            </h3>
            <br/>
            <h4 id="show" style={{ display: "none" }}> Género: {band.genreCode}  Año: {band.year}</h4>
          </div>
        ))}
        <br />
      </div>
    );
  }
};



export default ListBands;
