import React, { useState, useEffect } from "react";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Loading from "../components/Loading";
import { FaArrowCircleDown } from "react-icons/fa";
import Form from "react-bootstrap/Form";
import { Link } from 'react-router-dom'

const ListBands2 = () => {
  const [bands, setBands] = useState([]);
  const [allData, setAllData] = useState(false);
  const [searchTerm, setSearchTerm] = useState();


  useEffect(() => {
    obtainData();
  }, []);

  const obtainData = async () => {
    const data = await fetch(
      "https://my-json-server.typicode.com/improvein/dev-challenge/db"
    );
    const bandsName = await data.json();
    setBands(bandsName.bands);
    setAllData(true);
  };

  return (
    <div className="container-list">            
            <fieldset className="check-container">
            <Form.Group>
              <Form.Label as="legend">
              </Form.Label>
              <Form.Check
                type="radio"
                label="Bands"
                className="check"
                name="formHorizontalRadios"
                id="formHorizontalRadios1"
              />
              <Form.Check
                type="radio"
                label="Genres"
                className="check"
                name="formHorizontalRadios"
                id="formHorizontalRadios2"
              />
              <Form.Check
                type="radio"
                label="Years"
                className="check"
                name="formHorizontalRadios"
              />
            </Form.Group>
          </fieldset>

           <div>
          <Form id="search">
          <Form.Group>
            <Form.Control
            type="search" 
            placeholder="Search by band..ðŸ”"  
            onChange={(event) => { setSearchTerm(event.target.value); }}/>
            <br/>
          </Form.Group>
        </Form>
        </div>

            <div id="bands-title">
        <p>Â´Total Bands: {bands.length}</p>
        </div>
        { !allData ? <Loading/> :
      <div className="list-bands">
        <ul>
          {
               bands.filter(bands => {
                if(searchTerm === ""){
                  return bands
                }else if (bands.name.toLowerCase().includes(searchTerm.toLowerCase())){
                  return bands
                }else {
                  return(console.log("Error"))
                }
              })
          .map((item, idx) => (
            <h3 key={`key_${item.id}`}>
            <li>{idx + 1} - {item.name} </li>
            </h3>
          ))
          }
          
        </ul>
      </div>}
    </div>
  );
};

export default ListBands2;
