import React, { useState, useEffect } from "react";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Loading from "./Loading";
import { FaArrowCircleRight } from "react-icons/fa";
import Form from "react-bootstrap/Form";
import { Link } from "react-router-dom";
import Table from 'react-bootstrap/Table'

const ListBands2 = () => {
  const [bands, setBands] = useState([]);
  const [allData, setAllData] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    obtainData();
  }, []);

  const obtainData = async () => {
    const data = await fetch(
      "https://my-json-server.typicode.com/improvein/dev-challenge/db"
    );
    const bandsName = await data.json();
    setBands(bandsName.bands);
    setAllData(true);
  };

  return (
    <div className="container-list">
      <div id="bands-title">
        <p>´Total Bands: {bands.length}</p>
      </div>
      <fieldset className="check-container">
        <Form.Group>
          <Form.Label as="legend"></Form.Label>
          <Form.Check
            type="radio"
            label="Number"
            className="check"
            name="formHorizontalRadios"
            id="formHorizontalRadios1"
          />
          <Form.Check
            type="radio"
            label="Alphabetic"
            className="check"
            name="formHorizontalRadios"
            id="formHorizontalRadios2"
          />
                 </Form.Group>
                 <Form.Group>
            <Form.Check className="check-btn" type="checkbox" label="Rock" />
            <Form.Check className="check-btn" type="checkbox" label="Rock and Roll" />
            <Form.Check className="check-btn" type="checkbox" label="Hard Rock" />
            <Form.Check className="check-btn" type="checkbox" label="Grunge" />
            <Form.Check className="check-btn" type="checkbox" label="Heavy Metal" />
            <Form.Check className="check-btn" type="checkbox" label="Power Metal" />
            <Form.Check className="check-btn" type="checkbox" label="Black Metal" />
            <Form.Check className="check-btn" type="checkbox" label="Progressive Metal" />
        </Form.Group>
      </fieldset>

      <div>
        <Form id="search">
          <Form.Group>
            <Form.Control
              type="search"
              placeholder="Search by band..🔍"
              onChange={(event) => {
                setSearchTerm(event.target.value);
              }}
            />
            <br />
          </Form.Group>
        </Form>
      </div>

      {!allData ? (
        <Loading />
      ) : (
        <div className="list-bands">
          <ul>
            {bands
              .filter((item) => {
                if (searchTerm === "") {
                  return item;
                } else if (
                  item.name.toLowerCase().includes(searchTerm.toLowerCase())
                ) {
                  return item;
                } else {
                  return console.log("Error");
                }
              })
              .map((item, idx) => (
                <h3 key={`key_${item.id}`}>
                  <Table striped bordered hover variant="dark">
  <thead>
    <tr>
      <th>#</th>
      <th>Name</th>
      <th>Year</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>1</td>
      <td>Mark</td>
      <td>Otto</td>
    </tr>
    <tr>
      <td>2</td>
      <td>Jacob</td>
      <td>Thornton</td>
    </tr>
    <tr>
      <td>3</td>
      <td>Larry the Bird</td>
      <td>Thornton</td>

    </tr>
  </tbody>
</Table>
                  <li>
                    {idx + 1} - {item.name}
                    <Link to={`/menu/${item.id}`}>
                      <button className="btn-show">
                        <FaArrowCircleRight size="1em" />
                      </button>
                    </Link>
                  </li>
                </h3>
              ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ListBands2;
