import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "../css/Bands.css";
import Loading from "./Loading";


const Bands = () => {
  const { id } = useParams();
  const [infoBands, setInfoBands] = useState([]);
  const [membersBand, setMembersBand] = useState([]);
  const [albums, setAlbums] = useState([]);
  const [allData, setAllData] = useState(false);


  useEffect(() => {

      /*1st fetch*/

    const obtainDatas = async () => {
      const data = await fetch(
        `https://my-json-server.typicode.com/improvein/dev-challenge/bands/${id}`
      );

      const users = await data.json();
      setInfoBands(users);    
    };
     
    /*2nd fetch*/

    const obtainMembers = async () => {
      const data = await fetch(
        `https://my-json-server.typicode.com/improvein/dev-challenge/bands/${id}`
      );

      const memb = await data.json();
      setMembersBand(memb.members);
      setAllData(true);    

    };


        /*2nd fetch*/

        const obtainAlbums = async () => {
          const albums = await fetch(
            `https://my-json-server.typicode.com/improvein/dev-challenge/db`
          );
          const album = await albums.json();
          setAlbums(album.albums);
        };


    obtainAlbums([]);
    obtainDatas();
    obtainMembers([]);
  }, [id]);


  function showAlbums(){
  if(infoBands.id === albums.map((albumId) => albumId.bandId)){
    return (albums.map((album) => <h1>{album.name}</h1>))
  }else{
    <p>error</p>
  }
}

  return (
    <div className="container-bands">
      {!allData ? (
        <Loading />
      ) : (
      <div className="container-info">
      <h1><b className="info-band">Number:</b> {id}</h1>
      <h1> <b className="info-band">Name: </b> {infoBands.name}</h1>
      <h1><b className="info-band">Year:</b> {infoBands.year}</h1>
      <h1><b className="info-band"> Country: </b> {infoBands.country}</h1>
      <h1><b className="info-band"> Genre: </b>{infoBands.genreCode}</h1>
      <br/>
      <h1>Album names: {showAlbums()} </h1>
      <br/>
      <div className="members">
      <h1 id="members-title">Members:</h1>
      <br/>
       {membersBand.map((member) => (
        <h2><ul><li>{member.name}</li></ul></h2>
        ))}
      </div>
    </div>
    )}
    </div>
  );
};

export default Bands;
