import React, { useEffect, useState } from "react";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Button } from "react-bootstrap";
import Loading from "../components/Loading";
import { FaArrowCircleDown } from "react-icons/fa";
import axios from 'axios'


const ListBands = () => {
  const [loading, setLoading] = useState(false);
  const [dataBand, setDataBand] = useState([]);
  const [dataGenre, setDataGenre] = useState([])
  const [info, setInfo] = useState(false);
  const baseUrl = "https://my-json-server.typicode.com/improvein/dev-challenge";

  const changeState = () => {
    setLoading(true);
    setTimeout(()=>{
      setLoading(false)
    },1000)
  }

  const showInfo = () => {
    setInfo(!info);
  };

    const fetchData = async () => {
      const bands = `${baseUrl}/bands`;
      const genre = `${baseUrl}/genre`;
      
      const getBands = axios.get(bands);
      const getGenre = axios.get(genre);
      axios.all([getBands, getGenre]).then(
        axios.spread((...allData) => {
          const allDataBands = allData[0].data.name
          const getAllDataGenre = allData[1].config.url

          setDataBand(allDataBands)
          setDataGenre(getAllDataGenre)
        })
      )
  };

  useEffect(()=>{
    fetchData()
  }, [])


  try {
    if(info===true){
      document.getElementById('show').style.display = 'block'
    }else if(info===false){
      document.getElementById('show').style.display = 'none'
    }
  } catch (error) {
}

 
  if (loading) {
    return (
    <Loading />
    )

  } else {
    return (
      <div>
        <div><Button className="btn btn-light" onClick={()=>changeState()}>Refresh</Button></div>
        <h2 id="bands-title">Bands: {dataBand.length}</h2>
        <br />
        {dataBand.map((band, idx, genre) => (
          <div className="list-bands">
            <h3 key={`key_${band.id}`} code={`code_${band.genreCode}`} >
              {idx + 1} - {band.name} 
              <button className="btn-show" onClick={showInfo}><FaArrowCircleDown size="1em"/></button>
              <h4 id="show" style={{display:'none'}}> Genre: {genre.name} <br/>  Year: {band.year} <br/>  Country: {band.country} <br/> {} </h4>
            </h3>
            <br/>
          </div>
        ))}
        <br />
      </div>
    );
  }
};



export default ListBands;
