import React, { useEffect, useState } from "react";

const ListBands = () => {

  const [data, setData] = useState([]);
  const baseUrl = "https://my-json-server.typicode.com/improvein/dev-challenge";

  useEffect(() => {
    const fetchData = async () => {
      await fetch(`${baseUrl}/bands`)
        .then((response) => response.json())
        .then((json) => setData(json))
        .catch((error) => console.log(error));
    };
    fetchData();
    
  });

  const handleSubmit = async e => {
    this.setState({
        loading:true
    })
    e.preventDefault();
    try {
      let config = {
          method: 'POST',
          headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
          },
          body: JSON.stringify(this.state.form)
      }
      let res = await fetch(`${baseUrl}/bands`, config)
      let json = await res.json()
      console.log(json)
      this.setState({
          loading:false
      })
  
      this.props.history.push('/bands')
      
  } catch (error) {
      this.setState({
          loading:false,
          error
      })
  }
  };

  return (
      <>
     onSubmit={this.handleSubmit}
      <h3>Name of Bands {data.length}</h3>
      {data.map((band, idx) => (
        <div key={`key_${band.id}`}>{idx+1} - {band.name}</div>
      ))}
      </>
  )
};

export default ListBands;
