import React, { useState, useEffect } from "react";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Loading from "./Loading";
import { FaArrowCircleRight } from "react-icons/fa";
import Form from "react-bootstrap/Form";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import { MDBDataTable } from 'mdbreact';


const ListBands2 = () => {
  const [bands, setBands] = useState([]);
  const [allData, setAllData] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const dataTable = {
    columns: [{
      label: '#',
      field: 'id',
      sort: 'asc',
      width: 150
    },
    {
      label: 'Name',
      field: 'name',
      sort: 'asc',
      width: 270 
    },
    {
      label:'Year',
      field:'year',
      sort:'asc',
      width:270
    }
    ],
    rows:[{
        id:'1',
        name:'Jonathan',
        year: 23,
    },
    {
      label:'Year',
      field:'fsa',
      sort:'asc',
      width:270
    },
    {
      label:'Year',
      field:'asd',
      sort:'asc',
      width:270
    }
  ]
  }

  useEffect(() => {
    obtainData();
  }, []);

  const obtainData = async () => {
    const data = await fetch(
      "https://my-json-server.typicode.com/improvein/dev-challenge/db"
    );
    const bandsName = await data.json();
    setBands(bandsName.bands);
    setAllData(true);
  };

  return (
    <div className="container-list">
      <div id="bands-title">
        <p>´Total Bands: {bands.length}</p>
      </div>
      <fieldset className="check-container">
        <Form.Group>
          <Form.Check className="check" type="checkbox" label="Rock" />
          <Form.Check className="check" type="checkbox" label="Rock and Roll" />
          <Form.Check className="check" type="checkbox" label="Hard Rock" />
          <Form.Check className="check" type="checkbox" label="Grunge" />
          <Form.Check className="check" type="checkbox" label="Heavy Metal" />
          <Form.Check className="check" type="checkbox" label="Power Metal" />
          <Form.Check className="check" type="checkbox" label="Black Metal" />
          <Form.Check className="check" type="checkbox" label="ProgressMetal" />
        </Form.Group>
      </fieldset>

      <div>
        <Form id="search">
          <Form.Group>
            <Form.Control
              type="search"
              placeholder="Search by band..🔍"
              onChange={(event) => {
                setSearchTerm(event.target.value);
              }}
            />
            <br />
          </Form.Group>
        </Form>
      </div>

      {!allData ? (
        <Loading />
      ) : (
        <div className="list-bands">
          <ul>
          <MDBDataTable
      striped
      bordered
      small
      data={dataTable}
    />
          <Table id="table-data" striped bordered hover variant="dark">
                <thead>
                  <tr id="first-tr">
                    <th id="first-th">#</th>
                    <th id="second-th">Name </th>
                    <th id="third-th">Year</th>
                  </tr>
                </thead>
                <tbody>

            {bands
              .filter((item) => {
                if (searchTerm === "") {
                  return item;
                } else if ( item.name.toLowerCase().includes(searchTerm.toLowerCase()) ) {
                  return item;
                } else {
                  return null;
                }
              })
              .map((item, idx) => (
                  <tr>
                    <td>{idx + 1}</td>
                    <td>{item.name}
                    <Link to={`/menu/${item.id}`}>
                  <button className="btn-show">
                    <FaArrowCircleRight size="1em" />
                  </button>
                </Link></td>
                    <td>{item.year}</td>
                  </tr>
              ))}
              </tbody>
              </Table>
          </ul> 

        </div>
      )}
    </div>
  );
};

export default ListBands2;
