import React, { useEffect, useState } from "react";
import '../css/ListBands.css'
import "bootstrap/dist/css/bootstrap.min.css";
import { Button } from "react-bootstrap";
import ShowInfo from './ShowInfo'


const ListBands = () => {

  const [data, setData] = useState([]);
  const baseUrl = "https://my-json-server.typicode.com/improvein/dev-challenge";

  useEffect(() => {
    const fetchData = async () => {
      await fetch(`${baseUrl}/bands`)
        .then((response) => response.json())
        .then((json) => setData(json))
        .catch((error) => console.log(error));
    };
    fetchData();
    

  });

  return (
      <>
      <h2 id="bands-title">Bands: {data.length}</h2>
      <br/>
      {data.map((band, idx) => (
          <div className="list-bands">
        <h3 key={`key_${band.id}`}>{idx+1} - {band.name} <Button onClick={<ShowInfo/>} className="btn btn-danger">▼</Button></h3>
        <p id="info" style={{display:'none'}} key={`key_${band.genreCode}`}>{idx+1} - {band.year}</p>
        </div>
      ))}
      <br/>
      </>
  )
};

export default ListBands;
