/* eslint-disable array-callback-return */
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "../css/Bands.css";
import Loading from "./Loading";

const Bands = () => {
  const { id } = useParams();
  const [infoBands, setInfoBands] = useState([]);
  const [infoAlbums, setInfoAlbums] = useState([]);
  const [membersBand, setMembersBand] = useState([]);
  const [allData, setAllData] = useState(false);


  useEffect(() => {

    /*1st fetch*/
    const obtainDatas = async () => {
      const data = await fetch(
        `https://my-json-server.typicode.com/improvein/dev-challenge/bands/${id}`
      );

      const bands = await data.json();
      setInfoBands(bands);   
    };
    
    /*2nd fetch*/
    const obtainMembers = async () => {
      const data = await fetch(
        `https://my-json-server.typicode.com/improvein/dev-challenge/bands/${id}`
      );

      const memb = await data.json();
      setMembersBand(memb.members);
      setAllData(true);  
    };

    /*3rd fetch*/
        const obtainAlbums = async () => {
          const data = await fetch(
            `https://my-json-server.typicode.com/improvein/dev-challenge/db`
          );

          const album = await data.json();
          setInfoAlbums(album.albums);
        };
    
    obtainAlbums([]);
    obtainDatas([]);
    obtainMembers([]);

  }, [id]);


function showAlbums(){
 document.getElementById('show-album').style.visibility = 'visible';
}

  return (
    <div className="container-bands">
      {!allData ? (
        <Loading />
      ) : (
      <div className="container-info">
      <h1><b className="info-band">Number:</b> {infoBands.id}</h1>
      <h1> <b className="info-band">Name: </b> {infoBands.name}</h1>
      <h1><b className="info-band">Year:</b> {infoBands.year}</h1>
      <h1><b className="info-band"> Country: </b> {infoBands.country}</h1>
      <h1><b className="info-band"> Genre: </b>{infoBands.genreCode}</h1>
      <br/>

      <div className="info-album">
      <h1><button onClick={showAlbums()}>Show Albums</button>
      
        <b className="info-band">Albums:</b>
        <div id="show-album" style={{visibility:'hidden'}}>
        {infoAlbums.map((albumId) => {
      if(albumId.bandId === infoBands.id){
        return (<h1>{albumId.name}</h1>)
      }
    })}</div></h1>
    </div>

      <h1>{infoAlbums.map}</h1>
      <br/>
      <div className="members">
      <h1 id="members-title">Members:</h1>
      <br/>
       {membersBand.map((member) => (
        <h2><ul><li>{member.name}</li></ul></h2>
        ))}
      </div>
    </div>
    )}
    </div>
  );
};

export default Bands;
