import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const Bands = () => {
  const {id} = useParams()
  const [albums, setAlbums] = useState([])

  useEffect(() => {
    obtainData()
  },[])

  const obtainData = async () => {
    const data = await fetch(`https://my-json-server.typicode.com/improvein/dev-challenge/db/${id}`);
    const db = await data.json()
    setAlbums(db.albumsName)
  };

  return (
  <div className="container-bands">
      <h1>Band name: {albums.name}</h1>
      <h2>{albums.name}</h2>
      <h2>{albums.year}</h2>

  </div>
    )
};

export default Bands;
