import React, {useState} from 'react'
import { FaArrowCircleRight, FaArrowCircleDown} from "react-icons/fa";
import "../css/ListBands.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import Table from "react-bootstrap/Table";
import Form from 'react-bootstrap/Form'

function TableBands(props) {

  const [searchTerm, setSearchTerm] = useState("");
  const [searchBox, setSearchBox] = useState("");

  return(
    <div>
  <div className="search-container">
    <Form id="search">
    <Form.Group>
      <Form.Control
        type="search"
        placeholder="Search by band..üîç"
        onChange={(event) => {
          setSearchTerm(event.target.value);
        }}
      />
      <br />
    </Form.Group>
  </Form>
</div>

<div className="form-check">
  <input className="form-check-input" type="checkbox" value="" id="defaultCheck1"/>
  <label className="form-check-label" for="defaultCheck1">
    Default checkbox
  </label>
</div>

      <Table id="table-data" striped bordered hover variant="dark">
          <thead>
              <tr id="first-tr">
                  <th id="th-second">ID<button id="btn-circle" onClick={() => props.sortBy('id')}>
                    <FaArrowCircleDown id="icon-circle" color="#fff"/>
                    </button></th>
                  <th >Name</th>
                  <th id="th-second">Year<button id="btn-circle" onClick={() => props.sortBy('year')}>
                    <FaArrowCircleDown id="icon-circle" color="#fff"/>
                    </button></th>
              </tr>
          </thead>
          <tbody>
          {props.data
            .filter((item) => {
              if (searchTerm === "") {
                return item;
              } else if ( item.name.toLowerCase().includes(searchTerm.toLowerCase()) ) {
                return item;
              } else {
                return null;
              }
            })
            .map((item, idx) => (
              <tr>
                <td>{item.id}</td>
                <td>{item.name}
                <Link to={`/menu/${item.id}`}>
              <button className="btn-show">
                <FaArrowCircleRight size="1em" />
              </button>
            </Link></td>
                <td>{item.year}</td>
              </tr>
          ))
          }
          </tbody>
      </Table>
      </div>
  )
};

export default TableBands;
