import React from 'react'
import TableBands from './TableBands'
import Loading from "./Loading";
import "bootstrap/dist/css/bootstrap.min.css";
import { Animated } from 'react-animated'

class ListBands extends React.Component{

  constructor(props){
    super(props)
    this.state={
      data:[],
      allData: true,
      direction: {
        id:'asc',
        year:'asc',
        name :'asc',
      },
    }
    
    this.sortBy = this.sortBy.bind(this)
  }


  sortBy(key){
    this.setState({
      data: this.state.data.sort( (a, b) => (
        this.state.direction[key] === 'asc'
        ? parseInt(a[key]) - parseInt(b[key])
        : parseInt(b[key]) - parseInt(a[key])
      )),
        direction: {
          [key]: this.state.direction[key] === 'asc' ? 'desc' : 'asc'
      }})
    }

  async componentDidMount() {
    await this.fetchData()
  }

  fetchData = async () => {
    let res = await fetch('https://my-json-server.typicode.com/improvein/dev-challenge/bands')
    let data = await res.json()
    this.setState({
      data
    })

    this.setState({
      allData: false
    })
  }
  
  render(){
    return(
      <div className="container-list">
            <div id="bands-title">
               <p>Total Bands: <b style={{color:'#f91'}}>{this.state.data.length}</b></p>
             </div>
        <div className="list-bands">
        {this.state.allData ? ( <Loading/> ) : (
        <TableBands
        data={this.state.data} sortBy={this.sortBy} />
      )}</div>
      </div>
    )
  }
}

export default ListBands;
